# Dividir cada entrada por doble salto de línea
entries = raw_data.strip().split("\n\n")

formatted_data = {}

for entry in entries:
    # Dividir por tabuladores respetando saltos de línea dentro de los valores
    values = re.split(r'\t+', entry)
    values = [v.strip().replace("\n", " ") for v in values]  # Unir contenido separado por '\n' en el mismo campo
    
    # Asegurar que los valores coincidan con los headers
    if len(values) == len(headers):
        skill_name = values[0].title()  # Usar título como clave en el JSON
        formatted_data[skill_name] = dict(zip(headers, values))

# Convertir a JSON formateado
formatted_json = json.dumps(formatted_data, indent=4, ensure_ascii=False)

# Mostrar el resultado
print(formatted_json)